mapper.insert:
insert into
tbl_offline_sale
    (
    store_id
  , shoes_id
  , quantity
    )
values
    (
    #{storeId}
  , #{shoesId}
  , #{quantity}
    )

mapper.select:
SELECT DATE(sale_date) AS date, SUM(quantity) AS totalQuantity, SUM(quantity * sh.shoes_price) AS totalRevenue FROM tbl_offline_sale os JOIN tbl_shoes sh ON os.shoes_id = sh.shoes_id
os.store_id = #{storeId}
GROUP BY DATE(sale_date) ORDER BY date DESC LIMIT #{limit} OFFSET #{offset}

SELECT DATE_FORMAT(sale_date, '%Y-%m') AS date, SUM(quantity) AS totalQuantity, SUM(quantity * sh.shoes_price) AS totalRevenue FROM tbl_offline_sale os JOIN tbl_shoes sh ON os.shoes_id = sh.shoes_id
os.store_id = #{storeId}
GROUP BY DATE_FORMAT(sale_date, '%Y-%m') ORDER BY date DESC LIMIT #{limit} OFFSET #{offset}
